    /**
     * 获取Y年第N个节气日期
     * @param {number} y 公历年份 [1900-2100]
     * @param {number} n 第n个节气 [1-24]
     * @return {number} 日期(几号, 不含月份)
     */
    getSolarTermDate: function (y, n) {

        // 参数检查
        if (y < 1900 || y > 2100 || n < 1 || n > 24) {

            return -1

        }

        // 是否有缓存
        if (cache) {
            
        } else {
            
        }

        // 当年节气数据
        var thisTermData = this.solarTermsData[y - 1900];

        // 解码数据
        var tempStore = [];

        for (var i = 0; i < 6; i++) {

            var c = parseInt('0x' + thisTermData.substr(i * 5, 5).toString(), 10);

            tempStore.push(c);

        }

        // 节气日期数据
        var termsDateInfo = [
            tempStore[0].substr(0, 1),
            tempStore[0].substr(1, 2),
            tempStore[0].substr(3, 1),
            tempStore[0].substr(4, 2),

            tempStore[1].substr(0, 1),
            tempStore[1].substr(1, 2),
            tempStore[1].substr(3, 1),
            tempStore[1].substr(4, 2),

            tempStore[2].substr(0, 1),
            tempStore[2].substr(1, 2),
            tempStore[2].substr(3, 1),
            tempStore[2].substr(4, 2),

            tempStore[3].substr(0, 1),
            tempStore[3].substr(1, 2),
            tempStore[3].substr(3, 1),
            tempStore[3].substr(4, 2),

            tempStore[4].substr(0, 1),
            tempStore[4].substr(1, 2),
            tempStore[4].substr(3, 1),
            tempStore[4].substr(4, 2),

            tempStore[5].substr(0, 1),
            tempStore[5].substr(1, 2),
            tempStore[5].substr(3, 1),
            tempStore[5].substr(4, 2),
        ];


        // 写入缓存

        return parseInt(termsDateInfo[n - 1], 10);

    },

